import styled from "styled-components";
import moviesPoster from "../images/posters/movieshub.png";
import photohub from "../images/posters/photohub.png";
import restarunt from "../images/posters/restarunt.png";
import sayhi from "../images/posters/sayhi.png";
import wetherapp from "../images/posters/weatherapp.png";
const AllprojectsWapper = styled.div`
  width: 100%;
  display: grid;
  grid-template-columns: repeat(2, minmax(150px, 1fr));
  padding: 2rem;
  grid-gap: 3rem;
  & > div > div {
    width: 80%;
    margin: 0 auto;
    & > h1,
    h2,
    a {
      margin: 1rem 0;
    }
  }
`;
const Cardimg = styled.div`
  height: 30rem;
  background-color: red;
  background-image: url(${({ image }) => image});
  background-position: center;
  background-size: contain;
  background-repeat: no-repeat;
`;
const projectdata = [
  {
    name: "SayHI",
    info:
      "claims must be compatible with The Misconception of Deliberate Position",
    image: sayhi,
  },
  {
    name: "PhotoHub",
    info:
      "claims must be compatible with The Misconception of Deliberate Position",
    image: photohub,
  },
  {
    name: "Movies",
    info:
      "claims must be compatible with The Misconception of Deliberate Position",
    image: moviesPoster,
  },
  {
    name: "Weather App",
    info:
      "claims must be compatible with The Misconception of Deliberate Position",
    image: wetherapp,
  },
  {
    name: "Restaurant",
    info:
      "claims must be compatible with The Misconception of Deliberate Position",
    image: restarunt,
  },
];
const Allprojects = () => {
  return (
    <AllprojectsWapper>
      {projectdata.map(({ info, name, image }, i) => (
        <div key={i}>
          <div>
            <Cardimg image={image}></Cardimg>
            <h2>{name}</h2>
            <p>{info}</p>
          </div>
        </div>
      ))}
    </AllprojectsWapper>
  );
};

export default Allprojects;
