import styled from "styled-components";
import MainWitchLogo from "../images/whitelogo.svg";
import { useRouter } from "next/router";
const MainLayout = styled.div`
  display: flex;
  background-color: ${({ theme }) => theme.colors.blackBackground};
`;
const NavWapper = styled.div`
  flex-basis: 20%;
  height: 100vh;
  color: ${({ theme }) => theme.colors.textColor};
  position: sticky;
  top: 0;
  & > ul {
    height: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: space-evenly;
    list-style: none;
    font-size: 2.5rem;
    & > li {
      cursor: pointer;
    }
    & > li > a {
      color: ${({ theme }) => theme.colors.textColor};
      text-decoration: none;
    }
    & > li > svg {
      width: 5rem;
      height: auto;
    }
  }
`;
const LayOut = ({ children }) => {
  const router = useRouter();
  const { id } = router.query;
  console.log(id);
  return (
    <MainLayout>
      <NavWapper>
        <ul>
          <li>
            <MainWitchLogo />
          </li>
          <li onClick={() => document.getElementById("home").scrollIntoView()}>
            Home
          </li>
          <li
            onClick={() => document.getElementById("aboutme").scrollIntoView()}
          >
            About me
          </li>
          <li
            onClick={() =>
              document.getElementById("portfolio").scrollIntoView()
            }
          >
            Portfolio
          </li>
          <li
            onClick={() =>
              document.getElementById("contactme").scrollIntoView()
            }
          >
            Contact me
          </li>
        </ul>
      </NavWapper>
      {children}
    </MainLayout>
  );
};

export default LayOut;
